import React from 'react';
import { useState, useContext, useEffect } from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';

import Link from 'next/link';
import Router from 'next/router';

import Drawer from '@material-ui/core/Drawer';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import SettingsIcon from '@material-ui/icons/Settings';
import InsertChartIcon from '@material-ui/icons/InsertChart';
import SpaIcon from '@material-ui/icons/Spa';
import GolfCourseIcon from '@material-ui/icons/GolfCourse';
import CardGiftcardIcon from '@material-ui/icons/CardGiftcard';
import ShoppingBasketIcon from '@material-ui/icons/ShoppingBasket';
import DirectionsBikeIcon from '@material-ui/icons/DirectionsBike';
import DirectionsBusIcon from '@material-ui/icons/DirectionsBus';
import DirectionsBoatIcon from '@material-ui/icons/DirectionsBoat';
import GroupIcon from '@material-ui/icons/Group';
import AttachMoneyIcon from '@material-ui/icons/AttachMoney';
import ExitToAppIcon from '@material-ui/icons/ExitToApp';
import TranslateIcon from '@material-ui/icons/Translate';
import BuildIcon from '@material-ui/icons/Build';
import Avatar from '@material-ui/core/Avatar';
import Tooltip from '@material-ui/core/Tooltip';
import Menu from '@material-ui/core/Menu';
import FloatingMenuItem from '@material-ui/core/MenuItem';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Button from '@material-ui/core/Button';
import Box from '@material-ui/core/Box';
import { withStyles } from '@material-ui/core/styles';

const drawerWidth = 240;

const styles = (theme) => ({
  root: {
    minHeight: '100%',
    flexGrow: 1,
    zIndex: 1,
    overflow: 'hidden',
    position: 'relative',
    display: 'flex'
  },
  appBar: {
    zIndex: theme.zIndex.drawer + 1,
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen
    })
  },
  appBarShift: {
    marginLeft: drawerWidth,
    width: `calc(100% - ${drawerWidth}px)`,
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen
    })
  },
  menuButton: {
    marginLeft: 4,
    marginRight: 28
  },
  hide: {
    display: 'none'
  },
  drawerPaper: {
    position: 'relative',
    whiteSpace: 'nowrap',
    width: drawerWidth,
    transition: theme.transitions.create('width', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen
    })
  },
  drawerPaperClose: {
    overflowX: 'hidden',
    transition: theme.transitions.create('width', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen
    }),
    width: theme.spacing(7),
    [theme.breakpoints.up('sm')]: {
      width: theme.spacing(9)
    }
  },
  toolbar: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-end',
    padding: '0 8px',
    ...theme.mixins.toolbar
  },
  appVersion: {
    position: 'fixed',
    bottom: '0',
    left: '0',
    right: '0'
  },
  content: {
    display: 'flex',
    flexDirection: 'column',
    flexGrow: 1,
    backgroundColor: theme.palette.background.default,
    padding: theme.spacing(3),
    overflow: 'auto'
  },
  avatar: {
    cursor: 'pointer',
    margin: '0 auto',
    marginLeft: '2rem',
    width: 48,
    height: 48
  },
  smallAvatar: {
    cursor: 'pointer',
    margin: '0 auto',
    width: 48,
    height: 48
    //textTransform: 'lowercase',
  },
  listItem: {
    fontWeight: '100',
    transition: 'border 0.125s',
    '&:hover': {
      color: theme.palette.secondary.main,
      backgroundColor: 'transparent'
    }
  },
  listItemActive: {
    fontWeight: '100',
    color: theme.palette.secondary.main,
    backgroundColor: 'transparent'
  },
  colorUserName: {
    color: theme.palette.secondary.main
  },
  contentUserLogged: {
    marginTop: '0.5rem',
    marginBottom: '0rem',
    marginLeft: '0.5rem',
    marginRight: '5rem'
  },
  avatarContainer: {
    paddingTop: 28
  },
  inlineAvatar: {
    display: 'flex'
  },
  '@media (min-width: 600px)': {
    avatarContainer: {
      paddingTop: 8
    },
    menuButton: {
      marginLeft: 12,
      marginRight: 36
    },
    listItemIcon: {
      margin: '0 0.5rem'
    }
  }
});

function NetworkConnectionDisplayComponent(props) {
  const [online, setOnline] = useState(true);

  const updateNetworkStatus = () => {
    setOnline(window.navigator.onLine);
    console.log(window.navigator.onLine);
  };

  useEffect(() => {
    updateNetworkStatus();

    window.addEventListener('offline', updateNetworkStatus);
    window.addEventListener('online', updateNetworkStatus);

    return () => {
      window.removeEventListener('offline', updateNetworkStatus);
      window.removeEventListener('online', updateNetworkStatus);
    };
  }, []);

  return (
    <div
      style={{
        background: online ? '#64dd17' : '#b71c1c',
        textAlign: 'center',
        color: 'white',
        fontSize: '0.625rem',
        height: '2px'
      }}>
      &nbsp;
    </div>
  );
}

export const NetworkConnectionDisplay = withStyles(styles, { withTheme: true })(
  NetworkConnectionDisplayComponent
);

class MenuItemComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      hover: ''
    };
    this.refreshTokenTimer = 0;
  }

  render() {
    const { classes } = this.props;

    return (
      <ListItem
        onClick={this.props.onClick}
        classes={
          this.props.active || this.state.hover
            ? { root: classes.listItemActive }
            : { root: classes.listItem }
        }
        onMouseEnter={() => {
          this.setState({ hover: true });
        }}
        onMouseLeave={() => {
          this.setState({ hover: false });
        }}
        button>
        <ListItemIcon
          className={classes.listItemIcon}
          classes={
            this.props.active || this.state.hover
              ? { root: classes.listItemActive }
              : { root: classes.listItem }
          }>
          {this.props.icon}
        </ListItemIcon>
        <ListItemText
          primary={this.props.item}
          classes={
            this.props.active || this.state.hover
              ? { primary: classes.listItemActive }
              : { primary: classes.listItem }
          }
        />
      </ListItem>
    );
  }
}

const MenuItem = withStyles(styles, { withTheme: true })(MenuItemComponent);

function MiniDrawer(props) {
  const [open, setOpen] = useState(false);
  const [activeMenuItem, setActiveMenuItem] = useState('');
  const [avatarUrl, setAvatarUrl] = useState('');
  const [avatarLetter, setAvatarLetter] = useState('');
  const [username, setUsername] = useState('');
  const [title, setTitle] = useState('');
  const [loaded, setLoaded] = useState(false);
  const [languageSelectionMenuAnchorEl, setLanguageSelectionMenuAnchorEl] = useState(null);
  const [showLogoutConfirmationDialog, setShowLogoutConfirmationDialog] = useState(false);

  const language = useContext(DashboardLanguageContext);
  const userTokens = useContext(UserTokensContext);
  const userPermissions = useContext(UserPermissionsContext);

  let refreshTokenTimer = 0;

  const clearTokenRefresherTimeout = () => {
    if (refreshTokenTimer > 0) {
      window.clearTimeout(refreshTokenTimer);
      refreshTokenTimer = 0;
    }
  };

  const functionsBeforeUnload = () => {
    clearTokenRefresherTimeout();
  };





  useEffect(() => {
    if (
      Object.entries(userTokens.tokens).length === 0 &&
      userTokens.tokens.constructor === Object
    ) {
      Router.push({ pathname: '/dashboard/auth' });
      return;
    }

    const dataUser_string = localStorage.getItem('dataUser');

    validateRequiredPermissions(userPermissions.permissions, props.requiredPermissions);
    validateOptionalPermissions(userPermissions.permissions, props.optionalPermissions);

    setLoaded(true);

    const tokenRefreshedAt = localStorage.getItem('tokens_refreshed_at');
    if (tokenRefreshedAt !== null) {
      const dateTimeNow = new Date();
      const refreshTokenAfter = addHours(
        parse(tokenRefreshedAt, 'yyyy-MM-dd HH:mm:ss', new Date()),
        1
      );
      const millisecondsToRefresh = differenceInMilliseconds(refreshTokenAfter, dateTimeNow);

      if (millisecondsToRefresh < 0) {
        refreshToken();
      } else {
        // Seteamos timer para ejecutar la funcion en caso de que el usuario no cambie de pagina
        if (refreshTokenTimer > 0) {
          window.clearTimeout(refreshTokenTimer);
          refreshTokenTimer = 0;
        }
        refreshTokenTimer = window.setTimeout(() => {
          refreshToken();
        }, millisecondsToRefresh);
      }
    } else {
      refreshToken();
    }

    return functionsBeforeUnload;
  }, []);

  useEffect(() => {
    const now = Date.now();
    const url = window.location.href;

    const onbeforeunloadFn = () => {
      Util.track('page:unload', {
        url: url,
        duration: Date.now() - now
      });

      let metrics = Util.getMetrics();
      if (metrics.length > 0) {
        Util.storeMetrics(metrics);
      }
    };

    window.addEventListener('unload', onbeforeunloadFn);
    return () => {
      window.removeEventListener('unload', onbeforeunloadFn);
      Util.track('page:unload', {
        url: url,
        duration: Date.now() - now
      });
    };
  }, []);

  const handleDrawerOpen = () => {
    setOpen(true);
  };

  const handleDrawerClose = () => {
    setOpen(false);
  };

  const handleLogout = () => {
    setShowLogoutConfirmationDialog(false);

    const localStorageItemsToPreserve = [
      // List of items to keep in the localStorage
      'sales-position'
    ];

    const localStorageItems = Object.keys(localStorage);

    localStorageItems.map((item) => {
      if (localStorageItemsToPreserve.indexOf(item) == -1) {
        localStorage.removeItem(item);
      }
    });

    let metrics = Util.getMetrics();
    if (metrics.length > 0) {
      Util.storeMetrics(metrics);
    }
    sessionStorage.clear();

    // Update the user tokens context
    userTokens.setTokens({});
    userPermissions.setPermissions([]);

    Router.push({ pathname: '/dashboard/auth' });
  };

  const asksUserForConfirmationToLogout = () => {};

  const menuMouseOver = () => {
    setOpen(true);
  };

  const menuMouseOut = () => {
    setOpen(false);
  };

  const openLanguageSelectionMenu = (event) => {
    setLanguageSelectionMenuAnchorEl(event.currentTarget);
  };

  const closeLanguageSelectionMenu = (event) => {
    setLanguageSelectionMenuAnchorEl(null);
  };

  const selectLanguage = (lang) => () => {
    closeLanguageSelectionMenu();
    language.setLanguage(lang);
  };

  const { classes, theme } = props;

  if (loaded == false) {
    return false;
  }

  return (
    <div className={classes.root}>
      <Dialog
        open={showLogoutConfirmationDialog}
        onClose={() => {
          setShowLogoutConfirmationDialog(false);
        }}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description">
        <DialogTitle id="alert-dialog-title">
          <Typography component="span" variant="body1">
            {DashboardTranslations[language.value]['question-sure-to-exit-sistur']}
          </Typography>
        </DialogTitle>
        <DialogContent>
          <DialogContentText id="alert-dialog-description">
            <Typography component="span" variant="body2">
              {DashboardTranslations[language.value]['explanation-before-log-out']}
            </Typography>
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button
            onClick={() => {
              setShowLogoutConfirmationDialog(false);
            }}>
            {DashboardTranslations[language.value]['cancel']}
          </Button>
          <Button
            color="secondary"
            autoFocus
            variant="contained"
            onClick={() => {
              Util.track('LogoutConfirmationDialog#logoutButton[v1]:click', {});
              handleLogout();
            }}>
            {DashboardTranslations[language.value]['log-out']}
          </Button>
        </DialogActions>
      </Dialog>
