          <IconButton
            color="inherit"
            aria-label="Open drawer"
            onClick={handleDrawerOpen}
            className={classNames(classes.menuButton, open && classes.hide)}>
            <MenuIcon />
          </IconButton>
          <div
            style={{
              display: 'flex',
              flexGrow: '1',
              paddingRight: '1.5rem',
              alignItems: 'center'
            }}>
            {props.toolbar}
            <IconButton
              key={1}
              onClick={openLanguageSelectionMenu}
              color="inherit"
              title="Languages">
              <TranslateIcon />
            </IconButton>
            <Menu
              id="language-selector-menu"
              anchorEl={languageSelectionMenuAnchorEl}
              open={Boolean(languageSelectionMenuAnchorEl)}
              onClose={closeLanguageSelectionMenu}>
              <FloatingMenuItem onClick={selectLanguage('en-us')}>English</FloatingMenuItem>
              <FloatingMenuItem onClick={selectLanguage('es-mx')}>Espaol</FloatingMenuItem>
            </Menu>
            {props.newResource}
          </div>
        </Toolbar>
      </AppBar>
      <Drawer
        variant="permanent"
        classes={{
          paper: classNames(classes.drawerPaper, !open && classes.drawerPaperClose)
        }}
        open={open}
        onMouseLeave={() => menuMouseOut()}
        onMouseEnter={() => menuMouseOver()}>
        <div className={classes.toolbar}>
          <IconButton onClick={handleDrawerClose}>
            {theme.direction === 'rtl' ? <ChevronRightIcon /> : <ChevronLeftIcon />}
          </IconButton>
        </div>
        <List component="nav" className={classes.avatarContainer}>
          <div
            className={open ? classes.inlineAvatar : classes.onlyAvatar}
            onClick={() => {
              setActiveMenuItem('None');
            }}>
            <Link href="/dashboard/profile">
              <Avatar
                src={avatarUrl}
                className={open ? classes.avatar : classes.smallAvatar}
                style={{ backgroundColor: theme.palette.primary.main }}>
                {avatarLetter}
              </Avatar>
            </Link>
            <div className={open ? classes.contentUserLogged : classes.hide}>
              <Tooltip title={title} placement="right">
                <Typography
                  variant="h6"
                  gutterBottom
                  onClick={() => {
                    setActiveMenuItem('None');
                  }}>
                  <Link href="/dashboard/profile">
                    <a className={classes.colorUserName}>{username}</a>
                  </Link>
                </Typography>
              </Tooltip>
            </div>
          </div>
        </List>
        <Divider />
        <List component="nav">
          <AccessValidator
            userPermissions={userPermissions.permissions}
            optionalPermissions={['RAM_CONFIG_BASICO']}>
            <Link href="/dashboard/config">
              <MenuItem
                item={DashboardTranslations[language.value]['config']} //"Admin"
                onClick={() => {
                  setActiveMenuItem('Config');
                }}
                active={activeMenuItem == 'Config' ? true : false}
                icon={<BuildIcon />}
              />
            </Link>
          </AccessValidator>
          <AccessValidator
            userPermissions={userPermissions.permissions}
            optionalPermissions={['RAM_ADMIN_BASICO']}>
            <Link href="/dashboard/admin">
              <MenuItem
                item={DashboardTranslations[language.value]['admin']} //"Admin"
                onClick={() => {
                  setActiveMenuItem('Admin');
                }}
                active={activeMenuItem == 'Admin' ? true : false}
                icon={<SettingsIcon />}
              />
            </Link>
          </AccessValidator>
          <AccessValidator
            userPermissions={userPermissions.permissions}
            optionalPermissions={['ADMIN']}>
            <Link href="/dashboard/metrics">
              <MenuItem
                item={DashboardTranslations[language.value]['metrics']} //"Metrics"
                onClick={() => {
                  setActiveMenuItem('Metrics');
                }}
                active={activeMenuItem == 'Metrics' ? true : false}
                icon={<InsertChartIcon />}
              />
            </Link>
          </AccessValidator>
          <AccessValidator
            userPermissions={userPermissions.permissions}
            optionalPermissions={['RAM_PROMO_BASICO']}>
            <Link href="">
              <MenuItem
                item={DashboardTranslations[language.value]['promotions']} //"Promotions"
                onClick={() => {
                  setActiveMenuItem('Promotions');
                }}
                active={activeMenuItem == 'Promotions' ? true : false}
                icon={<CardGiftcardIcon />}
              />
            </Link>
          </AccessValidator>
          <AccessValidator
            userPermissions={userPermissions.permissions}
            optionalPermissions={['RAM_VENTAS_BASICO']}>
            <Link href="/dashboard/sales">
              <MenuItem
                item={DashboardTranslations[language.value]['sales']} //"Sales"
                onClick={() => {
                  setActiveMenuItem('Sales');
                }}
                active={activeMenuItem == 'Sales' ? true : false}
                icon={<ShoppingBasketIcon />}
              />
            </Link>
          </AccessValidator>
          <AccessValidator
            userPermissions={userPermissions.permissions}
            optionalPermissions={['RAM_OPSPA_BASICO', 'RAM_OPPVP_SPA']}>
            <Link href="/dashboard/spa">
              <MenuItem
                item="Spa"
                onClick={() => {
                  setActiveMenuItem('Spa');
                }}
                active={activeMenuItem == 'Spa' ? true : false}
                icon={<SpaIcon />}
              />
            </Link>
          </AccessValidator>
          <AccessValidator
            userPermissions={userPermissions.permissions}
            optionalPermissions={['RAM_GOLF_BASICO']}>
            <Link href="">
              <MenuItem
                item="Golf"
                onClick={() => {
                  setActiveMenuItem('Golf');
                }}
                active={activeMenuItem == 'Golf' ? true : false}
                icon={<GolfCourseIcon />}
              />
            </Link>
          </AccessValidator>
          <AccessValidator
            userPermissions={userPermissions.permissions}
            optionalPermissions={['RAM_OPTOUR_BASICO']}>
            <Link href="/dashboard/excursions">
              <MenuItem
                item={DashboardTranslations[language.value]['excursions']} //"Excursions"
                onClick={() => {
                  setActiveMenuItem('Excursions');
                }}
                active={activeMenuItem == 'Excursions' ? true : false}
                icon={<DirectionsBikeIcon />}
              />
            </Link>
          </AccessValidator>
          <AccessValidator
            userPermissions={userPermissions.permissions}
            optionalPermissions={['RAM_TRANS_BASICO']}>
            <Link href="/dashboard/transfers">
              <MenuItem
                item={DashboardTranslations[language.value]['transfers']} //"Transfers"
                onClick={() => {
                  setActiveMenuItem('Transfers');
                }}
                active={activeMenuItem == 'Transfers' ? true : false}
                icon={<DirectionsBusIcon />}
              />
            </Link>
          </AccessValidator>
          <AccessValidator
            userPermissions={userPermissions.permissions}
            optionalPermissions={['RAM_NAUTICO_BASICO']}>
            <Link href="/dashboard/nautic">
              <MenuItem
                item={DashboardTranslations[language.value]['nautic']} //"Nautic"
                onClick={() => {
                  setActiveMenuItem('Nautic');
                }}
                active={activeMenuItem == 'Nautic' ? true : false}
                icon={<DirectionsBoatIcon />}
              />
            </Link>
          </AccessValidator>
          <AccessValidator
            userPermissions={userPermissions.permissions}
            optionalPermissions={['RAM_GRPS_BASICO']}>
            <Link href="">
              <MenuItem
                item={DashboardTranslations[language.value]['group']} //"Group"
                onClick={() => {
                  setActiveMenuItem('Group');
                }}
                active={activeMenuItem == 'Group' ? true : false}
                icon={<GroupIcon />}
              />
            </Link>
          </AccessValidator>
          <AccessValidator
            userPermissions={userPermissions.permissions}
            optionalPermissions={['RAM_ANTICIPOS_BASICO']}>
            <Link href="">
              <MenuItem
                item={DashboardTranslations[language.value]['advances']} //"Advances"
                onClick={() => {
                  setActiveMenuItem('Advances');
                }}
                active={activeMenuItem == 'Advances' ? true : false}
                icon={<AttachMoneyIcon />}
              />
            </Link>
          </AccessValidator>
          <MenuItem
            item={DashboardTranslations[language.value]['log-out']}
            onClick={() => {
              setShowLogoutConfirmationDialog(true);
            }}
            active={false}
            icon={<ExitToAppIcon />}
          />
        </List>
      </Drawer>
      <main className={classes.content}>
        <div className={classes.toolbar}></div>
        {props.children}
        <div className={classes.appVersion}>
          <Typography
            style={{
              fontSize: '9px',
              textAlign: 'right',
              paddingRight: '4px'
            }}>
            {Config.version}
          </Typography>
          <Box display={{ xs: 'block', lg: 'none' }}>
            <NetworkConnectionDisplay />
          </Box>
        </div>
      </main>
    </div>
  );
}

MiniDrawer.propTypes = {
  classes: PropTypes.object.isRequired,
  theme: PropTypes.object.isRequired
};

export default withStyles(styles, { withTheme: true })(withSnackbar(MiniDrawer));
