import React from 'react';
import { useState, useContext, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Drawer from '@material-ui/core/Drawer';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import classNames from 'classnames';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import List from '@material-ui/core/List';
import Link from '@material-ui/core/Link'
import Avatar from '@material-ui/core/Avatar';

const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
  },
  appBar: {
    zIndex: theme.zIndex.drawer + 1,
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen
    })
  },
  appBarShift: {
    marginLeft: drawerWidth,
    width: `calc(100% - ${drawerWidth}px)`,
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen
    })
  },
  menuButton: {
    marginLeft: 4,
    marginRight: 28
  },
  hide: {
    display: 'none'
  },
  drawerPaper: {
    position: 'relative',
    whiteSpace: 'nowrap',
    width: drawerWidth,
    transition: theme.transitions.create('width', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen
    })
  },
  drawerPaperClose: {
    overflowX: 'hidden',
    transition: theme.transitions.create('width', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen
    }),
    width: theme.spacing(7),
    [theme.breakpoints.up('sm')]: {
      width: theme.spacing(9)
    }
  },
  toolbar: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-end',
    padding: '0 8px',
    ...theme.mixins.toolbar
  },
  avatarContainer: {
    paddingTop: 28
  },
  inlineAvatar: {
    display: 'flex'
  },
}));

export default function ButtonAppBar() {
  const [open, setOpen] = useState(false);
  const [avatarUrl, setAvatarUrl] = useState('');
  const [activeMenuItem, setActiveMenuItem] = useState('');
  const [avatarLetter, setAvatarLetter] = useState('');
  const classes = useStyles();
  const handleDrawerOpen = () => {
    setOpen(true);
  };
  const menuMouseOut = () => {
    setOpen(false);
  };
  const menuMouseOver = () => {
    setOpen(true);
  };
  const handleDrawerClose = () => {
    setOpen(false);
  };

  return (
    <div className={classes.root}>
      <AppBar
        position="absolute"
        className={classNames(classes.appBar, open && classes.appBarShift)}
        elevation={0}>
        <Toolbar disableGutters={!open}>
          <IconButton
            color="inherit"
            aria-label="Open drawer"
            onClick={handleDrawerOpen}
            className={classNames(classes.menuButton, open && classes.hide)}>
            <MenuIcon />
          </IconButton>
          <div
            style={{
              display: 'flex',
              flexGrow: '1',
              paddingRight: '1.5rem',
              alignItems: 'center'
            }}>
            <IconButton
              key={1}
              onClick={console.log('hola')}
              color="inherit"
              title="Languages">
            </IconButton>
          </div>
        </Toolbar>
      </AppBar>
      <Drawer
        variant="permanent"
        classes={{
          paper: classNames(classes.drawerPaper, !open && classes.drawerPaperClose)
        }}
        open={open}
        //onMouseLeave={() => menuMouseOut()}
        onMouseEnter={() => menuMouseOver()}>
        <div className={classes.toolbar}>
          <IconButton onClick={handleDrawerClose}>
            {!open ? <ChevronRightIcon /> : <ChevronLeftIcon />}
          </IconButton>
        </div>
        <List component="nav" className={classes.avatarContainer}>
          <div
            className={open ? classes.inlineAvatar : classes.onlyAvatar}
            onClick={() => {
              setActiveMenuItem('None');
            }}>
            <Link href="/dashboard/profile">
              <Avatar
                src={avatarUrl}
                className={open ? classes.avatar : classes.smallAvatar}
                style={{ backgroundColor: theme.palette.primary.main }}>
                {avatarLetter}
              </Avatar>
            </Link>
            <div className={open ? classes.contentUserLogged : classes.hide}>
              <Tooltip title={title} placement="right">
                <Typography
                  variant="h6"
                  gutterBottom
                  onClick={() => {
                    setActiveMenuItem('None');
                  }}>
                  <Link href="/dashboard/profile">
                    <a className={classes.colorUserName}>{username}</a>
                  </Link>
                </Typography>
              </Tooltip>
            </div>
          </div>
        </List>
      </Drawer>
    </div>
  );
}
