import React from 'react';
import { useState, useEffect, Fragment } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Axios from "axios";
import { Grid, Typography } from '@material-ui/core';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import IconButton from '@material-ui/core/IconButton';
import EditIcon from '@material-ui/icons/Edit';
import StarIcon from '@material-ui/icons/Star';
import CheckIcon from '@material-ui/icons/Check';
import Fab from '@material-ui/core/Fab';
import AddIcon from '@material-ui/icons/Add';


const useStyles = makeStyles((theme) => ({
  image: {
    width: 200,
    height: 220
  },
  spacer: {
    flex: '1 1 100%'
  },

}));

export default function Amazon(props) {
  const [data, setData] = useState({});
  const [activeTab, setactiveTab] = useState(0);
  const [loading, setLoading] = useState(false);
  const [idEdit, setidEdit] = useState();
  const [openModalEdit, setOpenModalEdit] = useState(false);
  function getData(){

    var config = {
      method: 'get',
      url: 'http://localhost:5000/consultar_normal',
      headers: { }
    };

    Axios(config)
    .then(function (response) {
      if(response.data.length>0){
        let date_agroup = restructuredData(response.data);
        console.log(date_agroup)
        setData(date_agroup);
      }
    })
    .catch(function (error) {
      console.log(error);
    });

  }
  function descargar_productos(){

    var config = {
      method: 'get',
      url: 'http://localhost:5000/consultar_normal',
      headers: { }
    };

    Axios(config)
    .then(function (response) {
      if(response.data.length>0){
        let date_agroup = restructuredData(response.data);
        console.log(date_agroup)
        setData(date_agroup);
      }
    })
    .catch(function (error) {
      console.log(error);
    });

  }
  const ChangeRouter = (id) => {
    setidEdit(id);
    setOpenModalEdit(true);
  };
  const changeActiveTab = (event, newActiveTab) => {
    setactiveTab(newActiveTab);
  };
  const restructuredData = (date_agroup) => {
    let agruop = {};
    date_agroup.forEach((categoria) => {
      if (categoria.categoria in agruop) {
        agruop[categoria.categoria] = agruop[categoria.categoria].concat([categoria]);
      } else {
        agruop[categoria.categoria] = [categoria];
      }
    });
    return agruop;
  };
  useEffect(() => {
    getData();
  }, []);

  const classes = useStyles();
  return (
    <Grid>
      {data && (
        <Grid container spacing={1}>
          <Grid item xs={12}>
            <Tabs
              value={activeTab}
              indicatorColor="primary"
              onChange={changeActiveTab}
              textColor="primary"
              style={{ marginBottom: '1.5rem' }}>
              {Object.keys(data).map((type) => {
                return (
                  <Tab
                    key={type}
                    label={type}
                  />
                );
              })}
            </Tabs>
          </Grid>
        </Grid>
      )}
      {data[Object.keys(data)[activeTab]] && !loading && (
        <Fragment>
          <Grid container spacing={2}>
            {data[Object.keys(data)[activeTab]].map((resource) => {
                return (
                  <Grid
                    style={{ display: 'grid', maxWidth: '20%' }}
                    key={resource.id_productos}
                    item xs={12} sm={6} md={4} lg={3} xl={2}>
                    <Grid
                      container
                      spacing={1}
                      className={classes.card}
                      style={resource.status == '1' ? {} : { background: '#f0f0f0' }}>
                        <Grid item xs={12}>
                          <Grid item xs={12} align="center">
                            <img style={{height: '50%', width: '50%' }}src={resource.coverImage} />
                          </Grid>
                          <Grid item xs={12} align="center">
                            <Typography className={classes.subtitle}>{resource.name}</Typography>
                          </Grid>
                          <Grid item xs={12} align="center">
                            <Typography className={classes.subtitle}>{resource.author}</Typography>
                          </Grid>
                          <Grid container justify="space-between">
                            <Grid item>
                              <Typography color="secondary" className={classes.title}>
                                {resource.id}
                              </Typography>
                            </Grid>
                            <Grid item>
                              <Grid container spacing={1}>
                                <Grid item sx={6}>
                                  <Grid container spacing={1}>
                                    <Grid item sx={6}>
                                      <StarIcon></StarIcon>
                                    </Grid>
                                    <Grid item sx={6}>
                                      <Typography className={classes.title}>
                                        {resource.value}
                                      </Typography>
                                    </Grid>
                                  </Grid>
                                </Grid>
                                <Grid item sx={6}>
                                  <Grid container spacing={1}>
                                    <Grid item sx={6}>
                                      <CheckIcon></CheckIcon>
                                    </Grid>
                                    <Grid item sx={6}>
                                      <Typography className={classes.title}>
                                        {resource.total}
                                      </Typography>
                                    </Grid>
                                  </Grid>
                                </Grid>
                              </Grid>
                            </Grid>
                          </Grid>
                        </Grid>      
                    </Grid>
                  </Grid>
                );
              })}
          </Grid>
        </Fragment>
      )}
      <div style={{ position: 'fixed', bottom: '1rem', right: '1rem', zIndex: 99999 }}>
        <Fab color="primary" onClick={descargar_productos()}>
          <AddIcon />
        </Fab>
      </div>
    </Grid>

  );
}
