import React from 'react';
import { useState, useEffect, Fragment } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Axios from "axios";
import { Grid, Typography } from '@material-ui/core';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import IconButton from '@material-ui/core/IconButton';
import EditIcon from '@material-ui/icons/Edit';


const useStyles = makeStyles((theme) => ({

}));

export default function Amazon(props) {
  const [data, setData] = useState({});
  const [activeTab, setactiveTab] = useState(0);
  const [page, setPage] = useState(0);
  const [loading, setLoading] = useState(false);
  const rowsPerPage = 40;
  function getData(){

    var config = {
      method: 'get',
      url: 'http://localhost:5000/consultar_normal',
      headers: { }
    };

    Axios(config)
    .then(function (response) {
      if(response.data.length>0){
        let date_agroup = restructuredData(response.data);
        console.log(date_agroup)
        setData(date_agroup);
      }
    })
    .catch(function (error) {
      console.log(error);
    });

  }
  const ChangeRouter = (id) => {
    setidEdit(id);
    setOpenModalEdit(true);
  };
  const changeActiveTab = (event, newActiveTab) => {
    setPage(0);
    setactiveTab(newActiveTab);
  };
  const restructuredData = (date_agroup) => {
    let agruop = {};
    date_agroup.forEach((categoria) => {
      if (categoria.categoria in agruop) {
        agruop[categoria.categoria] = agruop[categoria.categoria].concat([categoria]);
      } else {
        agruop[categoria.categoria] = [categoria];
      }
    });
    return agruop;
  };
  useEffect(() => {
    getData();
  }, []);

  const classes = useStyles();
  return (
    <Grid>
      {data && (
        <Grid container spacing={1}>
          <Grid item xs={12}>
            <Tabs
              value={activeTab}
              indicatorColor="primary"
              onChange={changeActiveTab}
              textColor="primary"
              style={{ marginBottom: '1.5rem' }}>
              {Object.keys(data).map((type) => {
                return (
                  <Tab
                    key={type}
                    label={type}
                  />
                );
              })}
            </Tabs>
          </Grid>
        </Grid>
      )}
      {data[Object.keys(data)[activeTab]] && !loading && (
        <Fragment>
          <Grid container spacing={2}>
            {data[Object.keys(data)[activeTab]]
              .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
              .map((resource) => {
                return (
                  <Grid
                    style={{ display: 'grid', maxWidth: '20%' }}
                    key={resource.id}
                    item
                    xs={12}
                    sm={6}
                    md={4}
                    lg={3}
                    xl={2}>
                    <Grid
                      container
                      spacing={1}
                      className={classes.card}
                      style={resource.status == '1' ? {} : { background: '#f0f0f0' }}>
                      <Grid item xs={12}>
                        <Grid container justify="space-between">
                          <Grid item>
                            <Typography color="secondary" className={classes.title}>
                              {resource.id}
                            </Typography>
                          </Grid>
                          <Grid item>
                            <Grid container spacing={1}>
                              <Grid item sx={6}>
                                <Grid container spacing={1}>
                                  <Grid item sx={6}>
                                    <Typography className={classes.title}>{'IATA: '}</Typography>
                                  </Grid>
                                  <Grid item sx={6}>
                                    <Typography className={classes.title}>
                                      {resource.iata_code}
                                    </Typography>
                                  </Grid>
                                </Grid>
                              </Grid>
                              |
                              <Grid item sx={6}>
                                <Grid container spacing={1}>
                                  <Grid item sx={6}>
                                    <Typography className={classes.title}>{'ICAO:'}</Typography>
                                  </Grid>
                                  <Grid item sx={6}>
                                    <Typography className={classes.title}>
                                      {resource.icao_code}
                                    </Typography>
                                  </Grid>
                                </Grid>
                              </Grid>
                            </Grid>
                          </Grid>
                        </Grid>
                      </Grid>
                      <Grid item xs={12}>
                        <Typography className={classes.subtitle}>{resource.name}</Typography>
                      </Grid>
                      <Grid item xs={12}>
                        <Grid container justify="flex-end">
                          <Grid item>
                            <IconButton
                              disabled={loading}
                              title={'editar'}
                              size="small"
                              color="secondary"
                              onClick={() => ChangeRouter(resource.id)}>
                              <EditIcon fontSize="inherit" />
                            </IconButton>
                          </Grid>
                        </Grid>
                      </Grid>
                    </Grid>
                  </Grid>
                );
              })}
          </Grid>
          <Grid item xs={12}>
            <Grid container spacing={1} justify="flex-end">
              <Paginator
                handleChangePage={(newPage) => setPage(newPage)}
                page={page}
                rowsLength={data[Object.keys(data)[activeTab]].length}
                rowsPerPage={rowsPerPage}
              />
            </Grid>
          </Grid>
        </Fragment>
      )}

    </Grid>

  );
}
